commands.cmd:
pcm.dmixerout {
  type dmix
  ipc_key 1024
  ipc_key_add_uid false
  slave {
    pcm "hw:CARDNAME,0"
    channels 2
    period_time 0
    period_size 1024
    buffer_size 4096
    rate 44100
  }
  bindings {
    0 0
    1 1
  }
}

pcm.dmixerloop {
  type dmix
  ipc_key 2048
  ipc_key_add_uid false
  slave {
    pcm "hw:Loopback,0,0"
    channels 2
    period_time 0
    period_size 1024
    buffer_size 4096
    format S32_LE
    rate 44100
  }
  bindings {
    0 0
    1 1
  }
}

pcm.quad {
  type multi
  slaves.a.pcm "dmixerout"
  slaves.a.channels 2
  slaves.b.pcm "dmixerloop"
  slaves.b.channels 2
  bindings {
    0 { slave a; channel 0; }
    1 { slave a; channel 1; }
    2 { slave b; channel 0; }
    3 { slave b; channel 1; }
  }
}

pcm.stereo2quad {
  type route
  slave.pcm "quad"
  ttable.0.0 1
  ttable.1.1 1
  ttable.0.2 1
  ttable.1.3 1
}
